
public class Operator01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 33;
		int n2 = 10;
		int result;

		result = n1 + n2;

		System.out.printf("%d %c %d = %d\n", n1, '+', n2, result);

		result = n1 - n2;

		System.out.printf("%d %c %d = %d\n", n1, '-', n2, result);

		result = n1 * n2;

		System.out.printf("%d %c %d = %d\n", n1, '*', n2, result);

		result = n1 / n2;

		System.out.printf("%d %c %d = %d\n", n1, '/', n2, result);

		result = n1 % n2;

		System.out.printf("%d %c %d = %d\n", n1, '%', n2, result);

	}

}
