
public class Operator02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 10;
		int n2 = n1++;
		
		System.out.printf("n1 = %d n2 = %d\n",n1,n2);
		
		int n3=10;
		
		System.out.println("n3 = "+n3++);
		System.out.println("n3 = "+n3);
		
		int n4=10;
		
		System.out.println("n4 = "+ ++n4);
		System.out.println("n4 = "+n4);
		
	}

}
