
public class Operator02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 10;
		int n2 = 5;

		int result = n1 & n2;
		System.out.println(result);

		result = n1 | n2;
		System.out.println(result);

		result = n1 ^ n2;
		System.out.println(result);
		
		result = ~n1 & n2;
		System.out.println(result);
	}

}
